//  Initiate a transmission flow.

//  Cycle though ring and dot icons, across various colors.
var fillSequence = ['green', 'green', 'yellow', 'yellow', 'blue', 'blue', 'grey', 'grey']
var shapeSequence = ['ring', 'dot'];
var sequence = flow.get('display-sequence') || 0;
var fill = fillSequence[sequence % fillSequence.length];
var shape = shapeSequence[sequence % shapeSequence.length];
flow.set('display-fill', fill);
flow.set('display-shape', shape);
flow.set('display-sequence', sequence + 1);

//  Initialise the logical device state.
//  Get the logical device state from the flow context.
var last_logical_device_state = flow.get('last_logical_device_state');
//  If the state is missing, store an empty state in the flow context.
if (!last_logical_device_state) {
    flow.set('last_logical_device_state', {});
}

//  Display the received payload.
node.status({
    fill: flow.get('display-fill'),
    shape: flow.get('display-shape'),
    text: JSON.stringify(msg.payload, null, 2)
});

//  Clear the status after 9 seconds.
setTimeout(function() {
    node.status({});
}, 9000);

//  Pass through the message with no changes.
return msg;

//  Construct a JSON payload containing the 
//  timestamp and sensor data.
var result = {
    //  We will be returning a payload
    //  instead of a top-level object.
    payload: {
        //  "timestamp" is number of milliseconds elapsed 
        //  since 1 Jan 1970 00:00:00 UTC.
        timestamp: Date.now(),

        //  "lig" is the light level, e.g. 500.
        lig: msg.payload
    }
};

//  Display the received payload.
node.status({
    //  Fill and shape are cycled by the "transmit" node.
    fill: flow.get('display-fill'),
    shape: flow.get('display-shape'),
    //  Convert the payload to JSON.
    text: JSON.stringify(result.payload)
});

//  Return the new message.
return result;

// Invert the level that is stored in the context.
// (context variables persist between calls to the function)
context.level = !context.level || false;

// Set the payload to the level and return.
msg.payload = context.level;

//  Display the payload.
node.status({
    text: 'Output is ' + JSON.stringify(msg.payload)
});
return msg;

//  Save the physical device state into memory.
flow.set('last_physical_device_state', msg.payload);

//  Display the physical device state saved.
node.status({
    //  Fill and shape are cycled by the "transmit" node.
    fill: flow.get('display-fill'),
    shape: flow.get('display-shape'),
    text: 'Saved state ' + JSON.stringify(msg.payload)
});

//  Pass through the original message.
return msg;

//  Display the payload.
node.status({
    //  Fill and shape are cycled by the "transmit" node.
    fill: flow.get('display-fill'),
    shape: flow.get('display-shape'),
    text: JSON.stringify(msg.payload, null, 2)
});

//  Pass through the message.
return msg;

//  Inject the timestamp if there was no timestamp.
//  If there was a saved state, inject the previous
//  fields that were not changed.
msg.payload = Object.assign(
    //  Object.assign will merge the following
    //  items in the sequence below.
    {},
    flow.get('last_logical_device_state'),
    { timestamp: Date.now() },
    msg.payload
);

//  Save the logical device state into memory.
